
public class MoreBucketMoreFun {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int dogs, cats, birds;
		
		birds = 3;
		cats = 10;
		
		 
		dogs  = birds + cats;
		
		System.out.println("There are " + birds + " birds.");
		System.out.println("There are " + cats + " cats.");
		System.out.println("There are " + dogs + " dogs.");
		System.out.println("Oh my cat ate a bird!!!");
		birds --;
		
		System.out.println("Now we have only " + birds+ " birds left.");

	}

}
